import React, { useEffect, useState } from 'react';
import './index.css';
import { getEmployees } from '../../services/api';
import EmployeeForm from '../../components/EmployeeForm';
// Assuming Header is located here, as implied by the EmployeeForm file
import Header from '../../components/Header'; 

function Employees() {
  const [employees, setEmployees] = useState([]);

  // Fetch employees data on component mount
  useEffect(() => {
    fetchEmployees();
  }, []);

  const fetchEmployees = async () => {
    try {
      const data = await getEmployees();
      setEmployees(data);
    } catch (error) {
      console.error("Failed to fetch employees:", error);
      // Optional: set an error state here to show user feedback
    }
  };

  return (
    <> {/* Use a Fragment to wrap the multiple top-level elements */}
      {/* ADDED HEADER HERE */}
      <Header /> 

      <div className="employees-container">
        <h2>Employees List</h2>
        
        {/* Pass fetchEmployees as a callback to refresh the list after adding an employee */}
        <EmployeeForm onAdd={fetchEmployees} /> 

        <table className="employee-table">
          <thead>
            <tr>
              <th>Name</th>
              <th>Email</th>
              <th>Phone</th>
              {/* Add an Actions column if you include Edit/Delete buttons */}
            </tr>
          </thead>
          <tbody>
            {employees.length > 0 ? (
              employees.map(emp => (
                <tr key={emp.id}>
                  <td>{emp.first_name} {emp.last_name}</td>
                  <td>{emp.email || 'N/A'}</td>
                  <td>{emp.phone || 'N/A'}</td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan="3" style={{ textAlign: 'center' }}>No employees found.</td>
              </tr>
            )}
          </tbody>
        </table>
      </div>
    </>
  );
}

export default Employees;
