import React, { useState } from 'react';
import api from '../../services/api';
import './index.css';

export default function EmployeeForm({ onSaved }){
  const [first_name,setFirst] = useState('');
  const [last_name,setLast] = useState('');
  const [email,setEmail] = useState('');
  const [phone,setPhone] = useState('');

  async function submit(e){
    e.preventDefault();
    try{
      await api.post('/employees', { first_name, last_name, email, phone });
      if(onSaved) onSaved();
    }catch(err){
      alert(err.response?.data?.message || 'Error');
    }
  }

  return (
    <div className="card" style={{marginTop:12}}>
      <h3>New Employee</h3>
      <form onSubmit={submit}>
        <div className="form-row"><label>First</label><input value={first_name} onChange={e=>setFirst(e.target.value)} /></div>
        <div className="form-row"><label>Last</label><input value={last_name} onChange={e=>setLast(e.target.value)} /></div>
        <div className="form-row"><label>Email</label><input value={email} onChange={e=>setEmail(e.target.value)} /></div>
        <div className="form-row"><label>Phone</label><input value={phone} onChange={e=>setPhone(e.target.value)} /></div>
        <button type="submit">Save</button>
      </form>
    </div>
  );
}
