import React, { useState } from 'react';
import api from '../../services/api';
import { useNavigate } from 'react-router-dom';
import './index.css';

export default function RegisterOrg(){
  const [orgName,setOrgName]=useState('');
  const [name,setName]=useState('');
  const [email,setEmail]=useState('');
  const [password,setPassword]=useState('');
  const nav = useNavigate();

  async function submit(e){
    e.preventDefault();
    try{
      const res = await api.post('/auth/register',{ orgName, adminName: name, email, password });
      localStorage.setItem('token', res.data.token);
      nav('/');
    }catch(err){ alert(err.response?.data?.message || 'Registration failed') }
  }

  return (
    <div className="card" style={{maxWidth:600, margin:'24px auto'}}>
      <h2>Create organisation</h2>
      <form onSubmit={submit}>
        <div className="form-row"><label>Organisation name</label><input value={orgName} onChange={e=>setOrgName(e.target.value)} /></div>
        <div className="form-row"><label>Admin name</label><input value={name} onChange={e=>setName(e.target.value)} /></div>
        <div className="form-row"><label>Email</label><input value={email} onChange={e=>setEmail(e.target.value)} /></div>
        <div className="form-row"><label>Password</label><input type="password" value={password} onChange={e=>setPassword(e.target.value)} /></div>
        <button type="submit">Create</button>
      </form>
    </div>
  );
}
