import React, { useMemo } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import './index.css';

// Custom hook for authentication state (no change needed here)
const useAuth = () => {
  const token = useMemo(() => localStorage.getItem('token'), []);
  return { isAuthenticated: !!token };
};

// Corrected: Using an arrow function expression
const Header = () => {
  const nav = useNavigate();
  const { isAuthenticated } = useAuth();

  function logout() {
    localStorage.removeItem('token');
    nav('/login');
  }

  return (
    <header className="header-colorful">
      {/* Brand/Logo Link */}
      <div className="header-brand-colorful">
        <Link to="/">HRMS</Link>
      </div>

      {/* Navigation Links Group */}
      <nav className="header-nav-colorful">
        {isAuthenticated ? (
          <>
            <Link to="/employees" className="nav-link-colorful">Employees</Link>
            <Link to="/teams" className="nav-link-colorful">Teams</Link>
            <button onClick={logout} className="nav-button-colorful nav-logout-colorful">
              Logout
            </button>
          </>
        ) : (
          <>
            <Link to="/login" className="nav-link-colorful">Login</Link>
            <Link to="/register" className="nav-link-colorful">
              Register
            </Link>
          </>
        )}
      </nav>
    </header>
  );
}

export default Header;
