import React, { useEffect, useState } from 'react';
import './index.css';
import { getTeams } from '../../services/api';
import TeamForm from '../../components/TeamForm';
import Header from '../Header';

function Teams() {
  const [teams, setTeams] = useState([]);
  const orgId = localStorage.getItem('orgId'); // fetch orgId

  useEffect(() => {
    fetchTeams();
  }, []);

  const fetchTeams = async () => {
    try {
      const data = await getTeams(orgId); // optionally pass orgId to backend
      setTeams(data);
    } catch (err) {
      console.error('Error fetching teams:', err);
      alert('Failed to load teams!');
    }
  };

  return (
    <div className="teams-container">
      <Header />
      <h2>Teams</h2>
      <TeamForm onAdd={fetchTeams} orgId={orgId} /> {/* pass orgId */}
      <table className="employee-table">
        <thead>
          <tr>
            <th>Name</th>
            <th>Description</th>
          </tr>
        </thead>
        <tbody>
          {teams.map(team => (
            <tr key={team.id}>
              <td>{team.name}</td>
              <td>{team.description || 'N/A'}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default Teams;
