import React, { useEffect, useState } from 'react';
import api from '../../services/api';
import './index.css';

export default function Teams(){
  const [teams,setTeams] = useState([]);
  const [name,setName] = useState('');
  const [desc,setDesc] = useState('');

  async function fetchTeams(){ const res = await api.get('/teams'); setTeams(res.data); }
  useEffect(()=>{ fetchTeams(); }, []);

  async function create(e){
    e.preventDefault();
    await api.post('/teams', { name, description: desc });
    setName(''); setDesc('');
    fetchTeams();
  }

  async function remove(id){
    if(!window.confirm('Delete team?')) return;
    await api.delete(`/teams/${id}`);
    fetchTeams();
  }

  return (
    <div>
      <div style={{display:'flex',justifyContent:'space-between',alignItems:'center'}}>
        <h2>Teams</h2>
      </div>

      <div className="card" style={{marginTop:12}}>
        <h4>Create Team</h4>
        <form onSubmit={create}>
          <div className="form-row"><label>Name</label><input value={name} onChange={e=>setName(e.target.value)} /></div>
          <div className="form-row"><label>Description</label><textarea value={desc} onChange={e=>setDesc(e.target.value)} /></div>
          <button type="submit">Create</button>
        </form>
      </div>

      <div className="list" style={{marginTop:12}}>
        {teams.map(t => (
          <div className="card" key={t.id}>
            <h4>{t.name}</h4>
            <p>{t.description}</p>
            <div style={{display:'flex', gap:8}}>
              <button onClick={()=>remove(t.id)}>Delete</button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
