import React, { useMemo } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import './index.css';

// Custom hook to handle auth state check
const useAuth = () => {
  const token = useMemo(() => localStorage.getItem('token'), []);
  return { isAuthenticated: !!token };
};

export default function Header() {
  const nav = useNavigate();
  const { isAuthenticated } = useAuth();

  function logout() {
    // Clear the token and redirect
    localStorage.removeItem('token');
    nav('/login');
  }

  return (
    <div className="header">
      {/* Brand/Logo Link */}
      <div className="header-brand">
        <Link to="/">HRMS</Link>
      </div>

      {/* Navigation Links Group */}
      <div className="header-nav">
        {isAuthenticated ? (
          <>
            <Link to="/employees" className="nav-link">Employees</Link>
            <Link to="/teams" className="nav-link">Teams</Link>
            {/* The Logout is an action, so button is appropriate */}
            <button onClick={logout} className="nav-button nav-logout">
              Logout
            </button>
          </>
        ) : (
          <>
            <Link to="/login" className="nav-link">Login</Link>
            <Link to="/register" className="nav-link nav-register">
              Register
            </Link>
          </>
        )}
      </div>
    </div>
  );
}
